/**
 * @param {string} string
 * @param {string} ref
 * @return {string}
 */
function minWindow(string, ref) {
  const refCountMap = {};
  const matchCountMap = { checkSet: new Set([]) };
  const refSet = new Set(ref.split(''));
  let lCursor = 0;
  const betterSubstr = { size: 0, lc: 0, rc: 0 };

  for (const char of ref) {
    refCountMap[char] = (refCountMap[char] ?? 0) + 1;
  }

  const addChar = (char) => {
    matchCountMap[char] = (matchCountMap[char] ?? 0) + 1;
    if (matchCountMap[char] >= refCountMap[char]) {
      matchCountMap.checkSet.add(char);
    }
  };

  const delChar = (char) => {
    matchCountMap[char] = (matchCountMap[char] ?? 0) - 1;
    if (matchCountMap[char] < refCountMap[char]) {
      matchCountMap.checkSet.delete(char);
    }
  };

  const isMatch = () => matchCountMap.checkSet.size === refSet.size;

  const handleMatch = (lc, rc) => {
    const substrSize = rc - lc + 1;
    if (!betterSubstr.size || substrSize < betterSubstr.size) {
      betterSubstr.size = substrSize;
      betterSubstr.lc = lc;
      betterSubstr.rc = rc;
    }
  };

  // move right cursor
  for (let rCursor = 0; rCursor <= string.length; rCursor += 1) {
    const char = string[rCursor];

    if (refCountMap[char]) {
      addChar(char);
    } else {
      continue;
    }

    if (isMatch()) {
      handleMatch(lCursor, rCursor);
    } else {
      // jump to next char
      continue;
    }

    // move left cursor & del char from
    for (
      delChar(string[lCursor]), lCursor += 1;
      lCursor <= rCursor;
      delChar(string[lCursor]), lCursor += 1
    ) {
      if (isMatch()) {
        handleMatch(lCursor, rCursor);
        // jump to next char
        continue;
      }

      // back to move right cursor
      break;
    }
  }

  return betterSubstr.size === 0 ? '' : string.substring(betterSubstr.lc, betterSubstr.rc + 1);
}

module.exports = () => {
  const res5 = minWindow('a', 'aa').length === 0;
  const res4 = minWindow('AADOBECODEBANC', 'ABC').length === 4;
  const res3 = minWindow('ab', 'b').length === 1;
  const res2 = minWindow('cabefgecdaecf', 'cae').length === 3;
  const res1 = minWindow('', '').length <= 952;

  console.log([res1, res2, res3, res4, res5].join('\n'));
};
